CREATE TABLE artwork_batch_items (id UUID PRIMARY KEY DEFAULT gen_random_uuid(), batch_id UUID REFERENCES artwork_batches(id) ON DELETE CASCADE, artwork_id UUID, name TEXT NOT NULL, file_url TEXT NOT NULL, file_type TEXT, file_size INTEGER, ai_analysis JSONB, status TEXT DEFAULT 'pending', customer_comment TEXT, approval_type TEXT, checklist JSONB, created_at TIMESTAMPTZ DEFAULT NOW(), updated_at TIMESTAMPTZ DEFAULT NOW());
